import{_ as c}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as d,c as a,a as e,b as o,d as n,e as s}from"./app-8cd661d7.js";const h={},i=e("h2",{id:"建站步骤",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#建站步骤","aria-hidden":"true"},"#"),o(" 建站步骤")],-1),p={href:"https://theme-hope.vuejs.press/zh/guide/get-started/intro.html",target:"_blank",rel:"noopener noreferrer"},_=s('<h2 id="部署的原理" tabindex="-1"><a class="header-anchor" href="#部署的原理" aria-hidden="true">#</a> 部署的原理</h2><p>在一个 Github 仓库创建两个分支，其中一个放源码，姑且称之为 <code>源码分支</code>，另一个放编译得到的 <code>html</code> 文件，称之为 <code>部署分支</code>。</p><p>设置 Github 工作流，当 <code>源码分支</code> 发生变动时，自动编译，将编译得到的 <code>html</code> 文件放到 <code>部署分支</code>。</p><p>在 Github Page 的编译和部署（Build and deployment），将来源选项（Source）设置为 <code>Deploy from a branch</code>；将分支选项（Branch）设置为 <code>部署分支</code>。</p><p>这样，只需要在 <code>源码分支</code> 创建内容，Github 会自动编译、部署到 <code>部署分支</code>，最终通过 Github Page 服务以网页的形式呈现。</p>',5);function l(u,m){const t=r("ExternalLinkIcon");return d(),a("div",null,[i,e("p",null,[o("参考官方文档："),e("a",p,[o("https://theme-hope.vuejs.press/zh/guide/get-started/intro.html"),n(t)])]),_])}const x=c(h,[["render",l],["__file","2023-08-16-Vuepress 建站记录.html.vue"]]);export{x as default};
